import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Container } from '@mui/system';
import { createTheme, CssBaseline, ThemeProvider, Typography } from "@mui/material";
import SMInput from "../../config/components/input";
import SMButton from "../../config/components/button";
import { editformData, sendData } from "../../config/firebase/firebasemethods";


function EditForm() {

  const theme = createTheme();
  const navigate = useNavigate()



  const location = useLocation();
  const [formEdit,setFormEdit] = useState(
   
    {
        class: location.state.class,
        section: location.state.section,
        dateofstart: location.state.dateofstart,
        dateofend: location.state.dateofend,
        qualification: location.state.qualification,
        id: location.state.id,
    }
    
  )
  


  const editfunc = () => {
    editformData(formEdit, 'course/',formEdit.id).then(() => {
        console.log("data update transfer")
        navigate("/news")

    }).catch((err) => {
        console.log(err)
    })

}


//   console.log(`STATE======>`,location.state)




 
  return (
    <Box>

<ThemeProvider theme={theme}>
    <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
            sx={{
                marginTop: 8,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
        >

            <Typography component="h1" variant="h5">
                Edit Form
            </Typography>
            <Box >
                <Box>

                <SMInput
                    margin="normal"
                    value={formEdit.class}
                    required
                    onChange={(e) => setFormEdit({ ...formEdit,class: e.target.value }) }
                    label="Class"
                    type="text"
                    id="password"
                    autoComplete="current-password"
                />
                </Box>
                <Box>

                <SMInput
                    margin="normal"
                    value={formEdit.section}
                    onChange={(e) => { setFormEdit({ ...formEdit,section: e.target.value }) }}
                    required
                    label="Section"
                    type="text"
                    autoFocus
                />
                </Box>
                <Box>
                    
                </Box>
                <Box></Box>
                <SMInput
                    margin="normal"
                    value={formEdit.dateofstart}
                    onChange={(e) => { setFormEdit({ ...formEdit, dateofstart: e.target.value }) }}
                    required
                    type="date"
                    autoComplete="current-password"
                />
                <SMInput
                    margin="normal"
                    value={formEdit.dateofend}
                    required
                    onChange={(e) => { setFormEdit({ ...formEdit, dateofend: e.target.value }) }}
                    type="date"
                    autoComplete="current-password"
                />
                <SMInput
                    margin="normal"
                    value={formEdit.qualification}
                    required
                    label="Required Qualification"
                    onChange={(e) => { setFormEdit({ ...formEdit, qualification: e.target.value }) }}
                    type="text"
                    autoComplete="current-password"
                />
                <SMButton
                    type="submit"
                    label="Update"
                    fullWidth
                    variant="contained"
                    onClick={editfunc}
                    sx={{ mt: 3, mb: 2 }}

                    Submit
                />

            </Box>
        </Box>
    </Container>
</ThemeProvider>
    </Box>
  );
}
export default EditForm;
